import React, { useState, PureComponent, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import axios from "axios";
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend
} from "recharts";

export default function Graph() {

    const [year_data, setYearData] = useState("");
    const [data, setData] = useState([]);
    const [name_data, setNameData] = useState([]);
    const [custom_data, setCustomData] = useState([]);
    const [flag, setFlag] = useState(false);

    const onFormSubmit = async (e) => {
        e.preventDefault();
        if (year_data) {
            try {
                const response = await axios.get(`http://localhost:5000/getcustom/${year_data}`);
                console.log(response.data);
            } catch (err) {
                console.log(err);
            }
        }
        else {
            alert("Complete all the Input Fields");
        }
    }

    const handleChange = (e) => {
        setYearData(e.target.value);
    }

    const getRecordByYear = async function () {
        const response = await axios.get("http://localhost:5000/getyear");
        if (response.status == 200) {
            setData(response.data);
        }
    }


    const getRecordByName = async function () {
        const response = await axios.get("http://localhost:5000/getname");
        if (response.status == 200) {
            setNameData(response.data);
        }
    }





    useEffect(() => {
        getRecordByYear();
        getRecordByName();
    }, []);

    return (
        <>
            <Header />
            <BarChart
                width={500}
                height={300}
                data={data}
                margin={{
                    top: 5,
                    right: 10,
                    left: 10,
                    bottom: 0
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="year" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="profit" fill="#00e673" />
                {/* <Bar dataKey="uv" fill="#82ca9d" /> */}
            </BarChart>
            <div></div>
            <BarChart
                width={500}
                height={300}
                data={name_data}
                margin={{
                    top: 5,
                    right: 10,
                    left: 10,
                    bottom: 0
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="profit" fill="#00e673" />
                {/* <Bar dataKey="uv" fill="#82ca9d" /> */}
            </BarChart>
            <Footer />
        </>
    )
}
