import React, { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import axios from "axios";
import {
    LineChart,
    ResponsiveContainer,
    Legend, Tooltip,
    Line,
    XAxis,
    YAxis,
    CartesianGrid
} from 'recharts';
export default function Graph() {

    const pdata = [
        {
            name: 'MongoDb',
            student: 11,
            fees: 120
        },
        {
            name: 'Javascript',
            student: 15,
            fees: 12
        },
        {
            name: 'PHP',
            student: 5,
            fees: 10
        },
        {
            name: 'Java',
            student: 10,
            fees: 5
        },
        {
            name: 'C#',
            student: 9,
            fees: 4
        },
        {
            name: 'C++',
            student: 10,
            fees: 8
        },
    ];

    const year = new Date().getFullYear();
    const [data, setData] = useState("");


    const onFormSubmit = async (e) => {
        e.preventDefault();
        if (data) {
            try {
                const response = await axios.get(`http://localhost:5000/getcustom/${data}`);
                console.log(response.data);
            } catch (err) {
                console.log(err);
            }
        }
        else {
            alert("Complete all the Input Fields");
        }
    }

    const handleChange = (e) => {
        setData(e.target.value);
    }


    return (
        <>
            <Header />
            <h1 className="text-heading">
                Line Chart Using Rechart
            </h1>
            <ResponsiveContainer width="100%" aspect={3}>
                <LineChart data={pdata} margin={{ left: 200, right: 200, top: 50 }}>
                    <CartesianGrid />
                    <XAxis dataKey="name"
                        interval={'preserveStartEnd'} />
                    <YAxis></YAxis>
                    <Legend />
                    <Tooltip />
                    <Line dataKey="student"
                        stroke="black" activeDot={{ r: 8 }} />
                    <Line dataKey="fees"
                        stroke="red" activeDot={{ r: 8 }} />
                </LineChart>
            </ResponsiveContainer>
            <form onSubmit={onFormSubmit}>
                <div class="flex flex-wrap -m-2">
                    <div class="p-2 w-1/2">
                        <div class="relative">
                            <label for="name" class="leading-7 text-sm text-gray-600">Year</label>
                            <input type="number" min="1900" max="2022" id="name" onChange={handleChange} value={data} name="year" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" />
                        </div>
                    </div>
                    <div class="p-8 w-full">
                        <button type="submit" class="flex mx-auto text-white bg-indigo-500 border-0 py-1 px-8 focus:outline-none hover:bg-indigo-600 rounded text-lg">Get</button>
                    </div>
                </div>
            </form>
            <Footer />
        </>
    )
}
