import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Table() {
    const [data, setData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    useEffect(() => {
        getData();
        getPriceData();
    }, [])
    const getData = async function () {
        const response = await axios.get("http://localhost:5000/record");
        if (response.status === 200) {
            setData(response.data);
        }
    }
    const getPriceData = function () {
        data && data.map((item, index) => {
            if (item.district == "Allahabad") {
                setPriceData([...priceData, item]);
            }
        })
    }
    function List(event) {
        return (
            <tr class="border-b">
                <td class="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{event.no}</td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.state}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.district}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.commodity}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.modal_price}
                </td>
            </tr>
        );
    }
    return (
        <div>
            <Header />
            <div class="flex flex-col">
                <div class="overflow-x-auto sm:-mx-6 lg:-mx-8">
                    <div class="py-2 inline-block min-w-full sm:px-6 lg:px-8">
                        <div class="overflow-hidden">
                            <table class="min-w-full">
                                <thead class="border-b">
                                    <tr>
                                        <th scope="col" class="text-sm font-medium text-gray-900 px-6 py-4 text-left">
                                            S No.
                                        </th>
                                        <th scope="col" class="text-sm font-medium text-gray-900 px-6 py-4 text-left">
                                            State
                                        </th>
                                        <th scope="col" class="text-sm font-medium text-gray-900 px-6 py-4 text-left">
                                            District
                                        </th>
                                        <th scope="col" class="text-sm font-medium text-gray-900 px-6 py-4 text-left">
                                            Commodity
                                        </th>
                                        <th scope="col" class="text-sm font-medium text-gray-900 px-6 py-4 text-left">
                                            Price
                                        </th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {priceData && priceData.map((item, index) => {
                                        return (
                                            <List no={index + 1} state={item.state} district={item.district} commodity={item.commodity} modal_price={item.modal_price} />
                                        );
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}
