import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Table() {
    const [data, setData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    useEffect(() => {
        getData();
    }, [])
    const getData = async function () {
        const response = await axios.get("http://localhost:5000/record");
        if (response.status === 200) {
            setData(response.data);
        }
        // getPriceData();
    }
    function List(event) {
        return (
            <tr class="border-b">
                <td class="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{event.no}</td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.state}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.district}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.commodity}
                </td>
                <td class="text-sm text-gray-900 font-light px-6 py-4 whitespace-nowrap">
                    {event.modal_price}
                </td>
            </tr>
        );
    }
    return (
        <div>

                                    {data && data.map((item, index) => {
                                        return (
                                            <List no={index + 1} state={item.state} district={item.district} commodity={item.commodity} modal_price={item.modal_price} />
                                        );
                                    })}

        </div>
    )
}
