import { React, useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
export default function Cattle() {
    const [myCar, setMyCar] = useState("Volvo");

    const onFormSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append("photo", file);
        const config = {
            headers: {
                "content-type": "multipart/form-data",
            },
        };
        const url = "http://localhost:5000/upload";
        axios.post(url, formData, config).then((response) => {
            console.log(response.data);
        }).catch((err) => {
            console.log('err', err);
        })
    }

    const handleChange = (event) => {
        setMyCar(event.target.value)
    }
    return (
        <>
            <Header />
            <section class="text-gray-600 body-font">
                <div class="container px-5 py-24 mx-auto">
                    <div class="flex flex-col text-center w-full mb-12">
                        <h1 class="sm:text-3xl text-2xl font-medium title-font mb-4 text-gray-900">Master Cleanse Reliac Heirloom</h1>
                        <p class="lg:w-2/3 mx-auto leading-relaxed text-base">Whatever cardigan tote bag tumblr hexagon brooklyn asymmetrical gentrify, subway tile poke farm-to-table. Franzen you probably haven't heard of them man bun deep.</p>
                    </div>
                    <div class="flex lg:w-2/3 w-full sm:flex-row flex-col mx-auto px-8 sm:space-x-4 sm:space-y-0 space-y-4 sm:px-0 items-end">
                        <div class="relative flex-grow w-full">
                            <form onSubmit={onFormSubmit}>
                                <label for="full-name" class="leading-7 text-sm text-gray-600">Full Name</label>
                                <select value={myCar} onChange={handleChange} class="block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 rounded shadow leading-tight focus:outline-none focus:shadow-outline">
                                    <option value="Ford">Ford</option>
                                    <option value="Volvo">Volvo</option>
                                    <option value="Fiat">Fiat</option>
                                </select>
                            </form>
                        </div>
                        <button class="text-white bg-indigo-500 border-0 py-2 px-8 focus:outline-none hover:bg-indigo-600 rounded text-lg">Button</button>
                    </div>
                </div>
            </section>
            <Footer />
        </>
    )
}
