import React, { useState, PureComponent, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import axios from "axios";
import {
    BarChart,
    Bar,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend
} from "recharts";

export default function Graph() {

    const year = new Date().getFullYear();
    const [year_data, setYearData] = useState("");
    const [data, setData] = useState([]);


    const onFormSubmit = async (e) => {
        e.preventDefault();
        if (year_data) {
            try {
                const response = await axios.get(`http://localhost:5000/getcustom/${year_data}`);
                console.log(response.data);
            } catch (err) {
                console.log(err);
            }
        }
        else {
            alert("Complete all the Input Fields");
        }
    }

    const handleChange = (e) => {
        setYearData(e.target.value);
    }

    const getRecord = async function () {
        const response = await axios.get("http://localhost:5000/getyear");
        if (response.status == 200) {
            console.log(response);
            setData(response.data);
        }
    }


    useEffect(() => {
        getRecord();
    }, []);

    // const data = [
    //     {
    //         name: 'Page A',
    //         uv: 4000,
    //         pv: 2400,
    //         amt: 2400,
    //     },
    //     {
    //         name: 'Page B',
    //         uv: 3000,
    //         pv: 1398,
    //         amt: 2210,
    //     },
    //     {
    //         name: 'Page C',
    //         uv: 2000,
    //         pv: 9800,
    //         amt: 2290,
    //     },
    //     {
    //         name: 'Page D',
    //         uv: 2780,
    //         pv: 3908,
    //         amt: 2000,
    //     },
    //     {
    //         name: 'Page E',
    //         uv: 1890,
    //         pv: 4800,
    //         amt: 2181,
    //     },
    //     {
    //         name: 'Page F',
    //         uv: 2390,
    //         pv: 3800,
    //         amt: 2500,
    //     },
    //     {
    //         name: 'Page G',
    //         uv: 3490,
    //         pv: 4300,
    //         amt: 2100,
    //     },
    // ];


    return (
        <>
            <Header />
            <BarChart
                width={500}
                height={300}
                data={data}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="pv" fill="#00e673" />
                {/* <Bar dataKey="uv" fill="#82ca9d" /> */}
            </BarChart>
            <div class="container px-5 py-16 mx-auto">
                <div class="lg:w-1/2 md:w-2/3 mx-auto">
                    <form onSubmit={onFormSubmit}>
                        <div class="flex flex-wrap -m-2">
                            <div class="p-2 w-1/2">
                                <div class="relative">
                                    <label for="name" class="leading-7 text-sm text-gray-600">Year</label>
                                    <input type="number" min="1900" max="2022" id="name" onChange={handleChange} value={data} name="year" class="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" />
                                </div>
                            </div>
                            <div class="p-8 w-full">
                                <button type="submit" class="flex mx-auto text-white bg-indigo-500 border-0 py-1 px-8 focus:outline-none hover:bg-indigo-600 rounded text-lg">Get</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <Footer />
        </>
    )
}
